    background-color: blue;
    width: 100%;
    height: 100vh;
    text-align: center;
    padding-top: 2%;


`

export const ImgError = styled.img`

   border: 2px;
   box-shadow: 6px 3px 10px 1px;
`

export const Title = styled.h1`
    color: lightgray;
    margin: 2%auto;
