import styled from "styled-components";


export const ContainerPage = styled.main`
    background-color: #223d6e;
    width: 100%;
    min-height: 100vh;
    align-items: center;
    text-align: center;


`
export const ErrorContainerPage = styled.div`
    width: 100%;

    align-items: center;

`

export const PicturePokeHome = styled.img`
width: 40px;
padding-left: 3%;
padding-top: 2%;
`
export const TitleContainer = styled.div`
    display: flex;
    text-align: center;
    justify-content: center;
    gap: 1%;
    padding-top: 2%



`

export const Container = styled.div`
    background-color: blue;
    width: 100%;
    height: 80vh;
    text-align: center;



`

export const ImgError = styled.img`

   border-radius: 50%;
   box-shadow: 4px 4px 8px 1px;
`

export const Title = styled.h1`
    color: lightgray;

    padding-bottom: 2%;
`
